declare module "avatar.enchant.js" {

    import { Sprite, Group, Surface } from "enchant.js";

    class AvatarCharacter extends Sprite {
        constructor(
            width: number,
            height: number,
        );
        action: string;
        animPattern: object;
        animFrame: number;
        left(): void;
        right(): void;
    }

    class AvatarMonster extends AvatarCharacter {
        constructor(image: Surface);
    }

    class AvatarBG extends Group {
        constructor(
            mode: 0 | 1 | 2 | 3,
            avatarBg1: string,
            avatarBg2: string,
            avatarBg3: string,
        );
        scroll(x: number): void;
    }

    // class Avatar extends AvatarCharacter {
    //     constructor(code: string);
    //     loadImage(): void;
    //     getCode(): string;
    //     setCode(code: string): void;
    // }
}
