declare module "nineleap.enchant.js" {
    import {
        Core as OrigCore,
        Scene as OrigScene,
    } from "enchant.js";

    declare class Core extends OrigCore {
        constructor(
            width: number,
            height: number,
            startImage: string,
            endImage: string,
        );
        end(
            playTimeSecs: number,
            message: string,
        ): void;
    }

    declare class SplashScene extends OrigScene {
        constructor();
        image: Surface;
    }
}