declare module "primitive.gl.enchant.js" {
    import { Sprite3D } from "gl.enchant.js";

    class Plane extends Sprite3D {
        constructor(scale?: number);
    }

    class PlaneXY extends Sprite3D {
        constructor(scale?: number);
    }

    class PlaneYZ extends Sprite3D {
        constructor(scale?: number);
    }

    class PlaneXZ extends Sprite3D {
        constructor(scale?: number);
    }

    class Billboard extends Plane {
        constructor(scale?: number);
    }

    class BillboardAnimation extends Billboard {
        constructor(
            divide?: number,
            scale?: number,
        );
        frame: number;
    }

    class BillboardY extends Plane {
        constructor(scale?: number);
    }

    class Box extends Sprite3D {
        constructor(
            sx?: number,
            sy?: number,
            sz?: number,
        );
    }

    class Cube extends Sprite3D {
        constructor(scale?: number);
    }

    class Sphere extends Sprite3D {
        constructor(
            r?: number,
            v?: number,
            h?: number,
        );
    }

    class Cylinder extends Sprite3D {
        constructor(
            r?: number,
            v?: number,
            h?: number,
        );
    }

    class Torus extends Sprite3D {
        constructor(
            r?: number,
            r2?: number,
            v?: number,
            v2?: number,
        );
    }
}
