declare module "ui.enchant.js" {
    import { Sprite, Group } from "enchant.js";

    declare class Pad extends Sprite {
        constructor(padImage: string);
    }

    declare class APad extends Group {
        constructor(
            mode: "direct" | "normal",
            padImage: string,
        );
    }

    declare class Button extends Entity {
        constructor(
            text: string,
            theme: string,
            height: number,
            width: number,
        );
        text: string;
        size: number;
        font: string;
        color: string;
        csvRender(): void;
        domRender(): void;
        theme2css(theme: ButtonTheme): object;
        static DEFAULT_THEME: {
            dark: ButtonThemes,
            light: ButtonThemes,
            blue: ButtonThemes,
        };
    }

    type ButtonThemes = {
        normal: ButtonTheme;
        active: ButtonTheme;
    };

    type ButtonTheme = {
        color: string;
        background: Background;
        border: Border;
        textShadow: Shadow;
        boxShadow: Shadow;
    };

    type Background = {
        type: string;
        start: string;
        end: string;
    };

    type Border = {
        color: string;
        width: number;
        type: string;
    };

    type Shadow = {
        offsetX: number;
        offsetY: number;
        blur: number;
        color: string;
    };

    declare class MutableText extends Sprite {
        constructor(
            x: number,
            y: number,
            fontImage: string,
        );
        setText(txt: string): void;
        text: string;
        row: number;
    }

    declare class ScoreLabel extends MutableText {
        constructor(
            x: number,
            y: number,
            fontImage: string,
        );
        score: number;
    }

    declare class TimeLabel extends MutableText {
        constructor(
            x: number,
            y: number,
            fontImage: string,
            counttype?: "countdown",
        );
        time: number;
    }

    declare class LifeLabel extends Group {
        constructor(
            x: number,
            y: number,
            maxlife: number,
            iconImage: string,
        );
        life: number;
    }

    declare class Bar extends Sprite {
        constructor(
            x: number,
            y: number,
        );
        direction: "right" | "left";
        x: number;
        maxvalue: number;
    }

    declare class VirtualMap extends Group {
        constructor(
            meshWidth: number,
            meshHeight: number,
        );
        addChild(obj: object): void;
        insertBefore(
            obj: object,
            reference: object,
        ): void;
        bind(obj: object): void;
    }

    declare function rand(num: number): number;
}